SELECT SUM("count") FROM (
SELECT COUNT(*) AS "count" FROM CRE_KPI_DEFINITION
WHERE KPI_NAME = #{kpiName,jdbcType=VARCHAR}

    AND KPI_ID != #{kpiId,jdbcType=VARCHAR}

UNION ALL
SELECT COUNT(*) AS "count" FROM CRE_VARIABLE
WHERE VARIABLE_ALIAS = #{kpiName,jdbcType=VARCHAR}
UNION ALL
SELECT COUNT(*) AS "count" FROM CRE_VARIABLE_ENTITY_RELATION
WHERE ENTITY_VARIABLE_ALIAS = #{kpiName,jdbcType=VARCHAR}
)

SELECT SUM("count") FROM (
SELECT COUNT(*) AS "count" FROM CRE_KPI_DEFINITION
WHERE KPI_CODE = #{kpiCode,jdbcType=VARCHAR}

    AND KPI_ID != #{kpiId,jdbcType=VARCHAR}

UNION ALL
SELECT COUNT(*) AS "count" FROM CRE_VARIABLE
WHERE VARIABLE_CODE = #{kpiCode,jdbcType=VARCHAR}
)

SELECT a.KPI_ID AS "kpiId", a.KPI_NAME AS "kpiName", a.KPI_CODE AS "kpiCode", a.KPI_TYPE AS "kpiType", a.KPI_DEFAULT_VALUE AS "kpiDefaultValue", a.KPI_DESC AS "kpiDesc", a.KPI_GROUP_ID AS "kpiGroupId", a.FETCH_TYPE AS "fetchType", b.KPI_GROUP_NAME AS "kpiGroupName" FROM CRE_KPI_DEFINITION a LEFT JOIN CRE_KPI_GROUP b ON a.KPI_GROUP_ID = b.KPI_GROUP_ID WHERE 1=1
AND a.KPI_NAME like #{pattern}
AND b.KPI_GROUP_NAME like #{pattern2}
AND a.KPI_TYPE = #{kpiType,jdbcType=VARCHAR}
AND a.FETCH_TYPE like #{fetchType,jdbcType=VARCHAR}
ORDER BY a.CREATE_DATE DESC

SELECT a.KPI_ID, a.KPI_NAME, a.KPI_CODE, a.KPI_TYPE, a.KPI_DEFAULT_VALUE , a.KPI_DESC, a.KPI_GROUP_ID, a.CREATE_DATE, a.UPDATE_DATE, a.CREATE_PERSON, a.UPDATE_PERSON, a.FETCH_TYPE, b.KPI_GROUP_NAME, a.DEPT_ID, a.DEPT_NAME, a.PARTNER_CODE, a.PARTNER_NAME, a.PRODUCT_CODE, a.PRODUCT_NAME, a.SYSTEM_CODE, a.SYSTEM_NAME, a.CREATE_USER_JOB_NUMBER, a.UPDATE_USER_JOB_NUMBER FROM CRE_KPI_DEFINITION a LEFT JOIN CRE_KPI_GROUP b ON a.KPI_GROUP_ID = b.KPI_GROUP_ID WHERE 1=1
AND a.KPI_NAME like #{pattern}
AND b.KPI_GROUP_NAME like #{pattern2}
AND a.KPI_TYPE = #{kpiType,jdbcType=VARCHAR}
AND a.FETCH_TYPE like #{fetchType,jdbcType=VARCHAR}
and a.CREATE_DATE >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and a.CREATE_DATE <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
ORDER BY a.CREATE_DATE DESC

SELECT a.KPI_ID, a.KPI_NAME, a.KPI_CODE, a.KPI_TYPE, a.KPI_DEFAULT_VALUE, a.KPI_DESC, a.FETCH_TYPE, a.KPI_VALUE_SOURCE, a.KPI_VALUE_SOURCE_NAME, a.KPI_VALUE_SOURCE_CODE, a.DB_ID, a.DB_ALIAS, a.DB_TYPE, a.TABLE_ID, a.TABLE_CODE, a.FETCH_SQL, a.API_ID, a.API_NAME, a.CREATE_DATE, a.UPDATE_DATE, a.CREATE_PERSON, a.UPDATE_PERSON, b.ID AS "limiters_ID", b.KPI_ID AS "limiters_KPI_ID", b.COLUMN_ID AS "limiters_COLUMN_ID", b.COLUMN_CODE AS "limiters_COLUMN_CODE", b.COLUMN_NAME AS "limiters_COLUMN_NAME", b.VARIABLE_ID AS "limiters_VARIABLE_ID", b.VARIABLE_CODE AS "limiters_VARIABLE_CODE", b.VARIABLE_NAME AS "limiters_VARIABLE_NAME", b.VARIABLE_TYPE_ID AS "limiters_VARIABLE_TYPE_ID", b.FETCH_TYPE AS "limiters_FETCH_TYPE" FROM ( SELECT c.KPI_ID as KPI_ID, c.KPI_NAME as KPI_NAME, c.KPI_CODE as KPI_CODE, c.KPI_TYPE as KPI_TYPE, c.KPI_DEFAULT_VALUE as KPI_DEFAULT_VALUE, c.KPI_DESC as KPI_DESC, c.FETCH_TYPE as FETCH_TYPE, c.KPI_VALUE_SOURCE as KPI_VALUE_SOURCE, c.KPI_VALUE_SOURCE_NAME as KPI_VALUE_SOURCE_NAME, c.KPI_VALUE_SOURCE_CODE as KPI_VALUE_SOURCE_CODE, c.DB_ID as DB_ID, c.DB_ALIAS as DB_ALIAS, c.DB_TYPE as DB_TYPE, c.TABLE_ID as TABLE_ID, c.TABLE_CODE as TABLE_CODE, c.FETCH_SQL as FETCH_SQL, c.API_ID as API_ID, c.API_NAME as API_NAME, c.CREATE_DATE as CREATE_DATE, c.UPDATE_DATE as UPDATE_DATE, c.CREATE_PERSON as CREATE_PERSON, c.UPDATE_PERSON as UPDATE_PERSON FROM CRE_KPI_DEFINITION c INNER JOIN CRE_KPI_RULE d ON c.KPI_ID = d.KPI_ID WHERE 1 = 1
AND d.RULE_ID = #{ruleId,jdbcType=VARCHAR}
) a LEFT JOIN CRE_KPI_FETCH_LIMITERS b ON a.KPI_ID = b.KPI_ID ORDER BY a.CREATE_DATE DESC

