    SELECT count(KPI_ID) AS "count" from CRE_KPI_RULE WHERE KPI_ID = #{kpiId,jdbcType=VARCHAR}
    UNION
    SELECT count(KPI_ID) AS "count" from CRE_KPI_RULE_SET WHERE KPI_ID = #{kpiId,jdbcType=VARCHAR}
)
