mapper.select:
select count(*) as "count",STATE as "state" from CRE_RULE_LOG where 1=1
and START_TIME >= TRUNC( SYSDATE - #{day} )
AND FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND DEPT_ID = #{deptId,jdbcType=VARCHAR}
GROUP BY STATE

select count(*) as "count", A.STATE as "state" from CRE_RULE_LOG A LEFT JOIN ( SELECT RULE_ID, RULE_NAME FROM CRE_RULE_DETAIL UNION ALL SELECT RULE_ID, RULE_NAME FROM CRE_RULE_DETAIL_DRAFT ) C ON A .RULE_ID = C.RULE_ID where 1=1 AND C.RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' )
and A.START_TIME >= TRUNC( SYSDATE - #{day} )
AND A.FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND A.DEPT_ID = #{deptId,jdbcType=VARCHAR}
GROUP BY STATE

SELECT "count", c.FOLDER_ID as "folderId", d.FOLDER_NAME as "folderName", c.RULE_NAME as "ruleName", c.MODULE_NAME as "moduleName" from ( SELECT count(*) as "count", a.FOLDER_ID, b.RULE_NAME,b.MODULE_NAME FROM (select FOLDER_ID,RULE_ID FROM CRE_RULE_LOG WHERE 1=1
and START_TIME >= TRUNC( SYSDATE - #{day} )
)a INNER JOIN ( SELECT RULE_NAME,MODULE_NAME, RULE_ID FROM CRE_RULE_DETAIL UNION ALL SELECT RULE_NAME,MODULE_NAME, RULE_ID FROM CRE_RULE_DETAIL_DRAFT ) b ON a.RULE_ID = b.RULE_ID GROUP BY a.FOLDER_ID, b.RULE_NAME,b.MODULE_NAME ) c INNER JOIN CRE_RULE_FOLDER d ON c.FOLDER_ID = d.FOLDER_ID ORDER BY "count" DESC

SELECT "count", c.FOLDER_ID as "folderId", d.FOLDER_NAME as "folderName", c.RULE_NAME as "ruleName", c.MODULE_NAME as "moduleName" from ( SELECT count(*) as "count", a.FOLDER_ID, b.RULE_NAME,b.MODULE_NAME FROM (select FOLDER_ID,RULE_ID FROM CRE_RULE_LOG WHERE 1=1
and START_TIME >= TRUNC( SYSDATE - #{day} )
)a INNER JOIN ( SELECT RULE_NAME,MODULE_NAME, RULE_ID FROM CRE_RULE_DETAIL UNION ALL SELECT RULE_NAME,MODULE_NAME, RULE_ID FROM CRE_RULE_DETAIL_DRAFT ) b ON a.RULE_ID = b.RULE_ID WHERE b.RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' ) GROUP BY a.FOLDER_ID, b.RULE_NAME,b.MODULE_NAME ) c INNER JOIN CRE_RULE_FOLDER d ON c.FOLDER_ID = d.FOLDER_ID ORDER BY "count" DESC

SELECT COUNT (DISTINCT(RULE_NAME)) AS modelCount, COUNT (VERSION) AS modelVersionCount, RULE_TYPE AS ruleType FROM ( SELECT RULE_NAME, VERSION, RULE_TYPE FROM CRE_RULE_DETAIL WHERE IS_DEL = '0' AND RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' ) UNION SELECT RULE_NAME, VERSION, RULE_TYPE FROM CRE_RULE_DETAIL_DRAFT WHERE IS_DEL = '0' AND RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' ) ) GROUP BY RULE_TYPE

SELECT COUNT (DISTINCT(RULE_NAME)) AS modelCount, RULE_STATUS AS ruleStatus FROM ( SELECT RULE_NAME, RULE_ID, CASE WHEN RULE_STATUS = '0' THEN '2' ELSE RULE_STATUS END AS RULE_STATUS FROM CRE_RULE_DETAIL WHERE IS_DEL = '0' AND IS_HEADER = '0' AND RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' ) ) GROUP BY RULE_STATUS

SELECT TASK_STATUS as status,count(*) as count from ( SELECT CASE WHEN TASK_STATUS = '2' THEN '4' WHEN TASK_STATUS = '-2' THEN '-1' ELSE TASK_STATUS END as TASK_STATUS FROM CRE_RULE_TASK WHERE TASK_STATUS != '6' AND TASK_ID IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID = '10' AND A .RESOURCE_ID != '*' ) ) GROUP BY TASK_STATUS
