SELECT DISTINCT(a.RESOURCE_EXPRESSION)
FROM CRE_AUTH_AUTHORITY a
WHERE a.SUBJECT_ID IN

    #{subjectId}

AND (
(a.RESOURCE_TYPE_ID = '0' AND a.RESOURCE_ID = '*') OR
(a.RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}

    AND (a.RESOURCE_ID = '*' OR a.RESOURCE_ID = #{resourceId,jdbcType=VARCHAR})

))

SELECT DISTINCT(a.RESOURCE_ID)
FROM CRE_AUTH_AUTHORITY a
WHERE a.SUBJECT_ID IN

    #{subjectId}

AND a.RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}
AND a.RESOURCE_ID != '*'

mapper.insert:
INSERT INTO CRE_AUTH_AUTHORITY ( ID, RESOURCE_ID, RESOURCE_TYPE_ID, RESOURCE_EXPRESSION, SUBJECT_ID, CREATE_DATE, CREATE_PERSON ) select A.* from (
select #{item.id,jdbcType=VARCHAR}, #{item.resourceId,jdbcType=VARCHAR}, #{item.resourceTypeId,jdbcType=VARCHAR}, #{item.resourceExpression,jdbcType=VARCHAR}, #{item.subjectId,jdbcType=VARCHAR}, #{item.createDate,jdbcType=TIMESTAMP}, #{item.createPerson,jdbcType=VARCHAR} from dual
) A

insert.foreach:
MERGE INTO CRE_AUTH_AUTHORITY USING dual ON ( RESOURCE_ID = #{item.resourceId,jdbcType=VARCHAR} AND RESOURCE_TYPE_ID = #{item.resourceTypeId,jdbcType=VARCHAR} AND SUBJECT_ID = #{item.subjectId,jdbcType=VARCHAR} ) WHEN MATCHED THEN UPDATE
RESOURCE_EXPRESSION = #{item.resourceExpression,jdbcType=VARCHAR}, UPDATE_DATE = #{item.updateDate,jdbcType=TIMESTAMP}, UPDATE_PERSON = #{item.updatePerson,jdbcType=VARCHAR}
WHEN NOT MATCHED THEN INSERT (ID, RESOURCE_ID, RESOURCE_TYPE_ID, RESOURCE_EXPRESSION, SUBJECT_ID, CREATE_DATE, CREATE_PERSON ) values ( #{item.id,jdbcType=VARCHAR}, #{item.resourceId,jdbcType=VARCHAR}, #{item.resourceTypeId,jdbcType=VARCHAR}, #{item.resourceExpression,jdbcType=VARCHAR}, #{item.subjectId,jdbcType=VARCHAR}, #{item.updateDate,jdbcType=TIMESTAMP}, #{item.updatePerson,jdbcType=VARCHAR} )

update.foreach:
UPDATE CRE_AUTH_AUTHORITY
RESOURCE_EXPRESSION = #{item.resourceExpression,jdbcType=VARCHAR}, UPDATE_DATE = #{item.updateDate,jdbcType=TIMESTAMP}, UPDATE_PERSON = #{item.updatePerson,jdbcType=TIMESTAMP}
WHERE RESOURCE_ID = #{item.resourceId,jdbcType=VARCHAR} AND RESOURCE_TYPE_ID = #{item.resourceTypeId,jdbcType=VARCHAR} AND SUBJECT_ID = #{item.subjectId,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM CRE_AUTH_AUTHORITY WHERE SUBJECT_ID = #{subjectId,jdbcType=VARCHAR}

    AND RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}


    AND RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}

DELETE FROM CRE_AUTH_AUTHORITY WHERE SUBJECT_ID = #{subjectId,jdbcType=VARCHAR}
AND RESOURCE_ID IN

    #{resourceId}

AND RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}

DELETE FROM CRE_AUTH_AUTHORITY WHERE
RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}
AND RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}

DELETE FROM CRE_AUTH_AUTHORITY WHERE
RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}
AND RESOURCE_ID IN

    #{resourceId}

mapper.update:
UPDATE CRE_AUTH_AUTHORITY

    RESOURCE_ID = #{newResourceId,jdbcType=VARCHAR}

WHERE
RESOURCE_TYPE_ID = #{resourceTypeId,jdbcType=VARCHAR}
AND RESOURCE_ID = #{oldResourceId,jdbcType=VARCHAR}
