mapper.select:
select RULE_SET_ID, VERSION
from CRE_PUB_RULE_SET
where RULE_SET_HEADER_ID = #{ruleSetHeaderId,jdbcType=VARCHAR}

select count(*) as "count" from CRE_PUB_RULE_SET_HEADER
where RULE_SET_NAME = #{ruleSetName,jdbcType=VARCHAR}

    AND RULE_SET_HEADER_ID != #{ruleSetHeaderId,jdbcType=VARCHAR}

select count(*) as "count" from CRE_PUB_RULE_SET
where VERSION = #{version,jdbcType=VARCHAR}
AND RULE_SET_HEADER_ID = #{ruleSetHeaderId,jdbcType=VARCHAR}

    AND RULE_SET_ID != #{ruleSetId,jdbcType=VARCHAR}

SELECT COUNT(1) FROM CRE_PUB_RULE_SET WHERE
RULE_SET_HEADER_ID = #{ruleSetHeaderId,jdbcType=VARCHAR}
AND "ENABLE" = '1'

SELECT COUNT(1) FROM CRE_PUB_RULE_SET WHERE
RULE_SET_ID = #{ruleSetId,jdbcType=VARCHAR}
AND "ENABLE" = '1'

SELECT a.RULE_SET_HEADER_ID, a.RULE_SET_NAME, a.RULE_SET_GROUP_ID, a.RULE_SET_HEADER_DESC, a.CREATE_DATE, a.UPDATE_DATE, a.CREATE_PERSON, a.UPDATE_PERSON, b.RULE_SET_GROUP_NAME FROM CRE_PUB_RULE_SET_HEADER a LEFT JOIN CRE_PUB_RULE_SET_GROUP b ON a.RULE_SET_GROUP_ID = b.RULE_SET_GROUP_ID WHERE 1=1
AND a.RULE_SET_NAME like #{pattern}
AND b.RULE_SET_GROUP_NAME like #{pattern2}
and a.CREATE_DATE >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and a.CREATE_DATE <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
ORDER BY CREATE_DATE DESC

SELECT a.RULE_SET_HEADER_ID AS "ruleSetHeaderId", a.RULE_SET_NAME AS "ruleSetName", a.RULE_SET_GROUP_ID AS "ruleSetGroupId", a.RULE_SET_HEADER_DESC AS "ruleSetHeaderDesc", a.CREATE_DATE AS "createDate", a.UPDATE_DATE AS "updateDate", a.CREATE_PERSON AS "createPerson", a.UPDATE_PERSON AS "updatePerson", b.RULE_SET_GROUP_NAME AS "ruleSetGroupName" FROM CRE_PUB_RULE_SET_HEADER a LEFT JOIN CRE_PUB_RULE_SET_GROUP b ON a.RULE_SET_GROUP_ID = b.RULE_SET_GROUP_ID WHERE 1=1
AND a.RULE_SET_NAME like #{pattern}
AND b.RULE_SET_GROUP_NAME like #{pattern2}
and a.CREATE_DATE >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and a.CREATE_DATE <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
ORDER BY a.CREATE_DATE DESC

SELECT
FROM CRE_PUB_RULE_SET a LEFT JOIN CRE_PUB_RULE_SET_HEADER b ON a.RULE_SET_HEADER_ID = b.RULE_SET_HEADER_ID WHERE a.RULE_SET_HEADER_ID = #{ruleSetHeaderId,jdbcType=VARCHAR}
and a.ENABLE = #{enable,jdbcType=VARCHAR}
and a.CREATE_DATE >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and a.CREATE_DATE <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
ORDER BY a.CREATE_DATE DESC
