mapper.select:
SELECT a.KPI_ID AS "kpiId", b.KPI_CODE AS "kpiCode",b.KPI_TYPE AS "kpiType", b.KPI_NAME AS "kpiName", b.KPI_TYPE AS "kpiType",b.KPI_DESC AS "kpiDesc",a.KPI_RANGE AS "kpiRange" FROM CRE_KPI_RULE a LEFT JOIN CRE_KPI_DEFINITION b ON a.KPI_ID = b.KPI_ID WHERE 1=1
AND RULE_ID = #{ruleId,jdbcType=VARCHAR}
AND RULE_ID IN ( select RULE_ID from CRE_RULE_DETAIL WHERE IS_DEL = '0' AND IS_HEADER = '0' AND RULE_NAME =#{ruleName,jdbcType=VARCHAR} UNION ALL select RULE_ID from CRE_RULE_DETAIL_DRAFT WHERE IS_DEL = '0' AND IS_HEADER = '0' AND RULE_NAME = #{ruleName,jdbcType=VARCHAR})

mapper.insert:
insert into CRE_KPI_RULE (
KPI_ID, RULE_ID,KPI_RANGE
) select A.* from (

    select
    #{item.kpiId,jdbcType=VARCHAR},
    #{item.ruleId,jdbcType=VARCHAR},
    #{item.kpiRange,jdbcType=VARCHAR}
    from dual
) A

mapper.update:
update CRE_KPI_RULE
SET
RULE_ID = #{ruleId,jdbcType=VARCHAR}

    ,KPI_RANGE = #{kpiRange,jdbcType=VARCHAR}

where RULE_ID = #{oldVariableId,jdbcType=VARCHAR}
AND KPI_ID = #{kpiId,jdbcType=VARCHAR}

mapper.delete:
delete from CRE_KPI_RULE
where (

    RULE_ID = #{item.ruleId,jdbcType=VARCHAR} AND KPI_ID = #{item.kpiId,jdbcType=VARCHAR}

)
