mapper.select:
SELECT * FROM ( select a.TABLE_ID as tableId,a.TABLE_CODE as tableCode,a.TABLE_NAME as tableName, a.TABLE_TYPE as tableType,a.TABLE_KIND as tableKind,a.DB_ID as dbId,a.IS_TEMP as isTemp, a.IS_PUBLIC as isPublic, a.UPDATE_DATE as updateDate, a.CREATE_DATE As createDate, b.DB_ALIAS as dbAlias, b.DB_TYPE as dbType, a.SCAN_ID as scanId from CRE_META_TABLE a left join CRE_DB_CONNECTION b on a.DB_ID = b.DB_ID where 1=1
AND a.PACKAGE_ID = #{packageId,jdbcType=VARCHAR}
union SELECT a.TABLE_ID AS tableId, a.TABLE_CODE AS tableCode,a.TABLE_NAME AS tableName, a.TABLE_TYPE AS tableType,a.TABLE_KIND AS tableKind,a.DB_ID AS dbId,a.IS_TEMP AS isTemp, a.IS_PUBLIC AS isPublic,a.UPDATE_DATE AS updateDate,a.CREATE_DATE AS createDate, b.DB_ALIAS AS dbAlias, b.DB_TYPE as dbType, a.SCAN_ID as scanId FROM CRE_META_TABLE a LEFT JOIN CRE_DB_CONNECTION b ON a.DB_ID = b.DB_ID WHERE a.IS_PUBLIC = '1' AND PACKAGE_ID = ( SELECT FOLDER_PID FROM CRE_RULE_FOLDER WHERE 1=1
AND FOLDER_ID = #{packageId,jdbcType=VARCHAR}
) ) t ORDER BY t.createDate

SELECT a.TABLE_ID AS "tableId", a.TABLE_NAME AS "tableName", b.DB_ID AS "dbId", b.DB_ALIAS AS "dbAlias", c.FOLDER_ID AS "folderId", c.FOLDER_NAME as "folderName" FROM CRE_META_TABLE a LEFT JOIN CRE_DB_CONNECTION b ON a.DB_ID = b.DB_ID LEFT JOIN CRE_RULE_FOLDER c ON a.PACKAGE_ID = c.FOLDER_ID WHERE 1=1
and a.TABLE_NAME like #{tableNamePattern}
and b.DB_ALIAS like #{dbAliasPattern}
and c.FOLDER_NAME like #{folderNamePattern}
ORDER BY a.CREATE_DATE DESC

select count(*)
from CRE_META_TABLE A,(
SELECT B.FOLDER_PID AS packageId
FROM CRE_RULE_FOLDER B
WHERE B.FOLDER_ID = #{packageId,jdbcType=VARCHAR}
AND B.FOLDER_PID IS NOT NULL
UNION
SELECT B.FOLDER_ID AS packageId
FROM CRE_RULE_FOLDER B
WHERE B.FOLDER_ID = #{packageId,jdbcType=VARCHAR}) T
where A.PACKAGE_ID = T.packageId
AND A.TABLE_NAME = #{tableName,jdbcType=VARCHAR}

    and A.TABLE_ID != #{tableId,jdbcType=VARCHAR}

select count(*)
from CRE_META_TABLE A,(
SELECT B.FOLDER_PID AS packageId
FROM CRE_RULE_FOLDER B
WHERE B.FOLDER_ID = #{packageId,jdbcType=VARCHAR}
AND B.FOLDER_PID IS NOT NULL
UNION
SELECT B.FOLDER_ID AS packageId
FROM CRE_RULE_FOLDER B
WHERE B.FOLDER_ID = #{packageId,jdbcType=VARCHAR}) T
where A.PACKAGE_ID = T.packageId
AND A.TABLE_CODE = #{tableName,jdbcType=VARCHAR}

    and A.TABLE_ID != #{tableId,jdbcType=VARCHAR}

  	SELECT 
		a.TABLE_ID as id,a.DB_ID as dbId,a.TABLE_CODE as tableCode,a.TABLE_NAME as name
	FROM CRE_META_TABLE a
	WHERE a.PACKAGE_ID = #{packageId,jdbcType=VARCHAR}
	and a.DB_ID = #{dbId,jdbcType=VARCHAR}

  	SELECT TABLE_CODE as name FROM CRE_META_TABLE
	WHERE PACKAGE_ID = #{packageId,jdbcType=VARCHAR}
	UNION ALL
	SELECT DISTINCT A.COLUMN_CODE as name FROM CRE_META_COLUMN A
	WHERE A.TABLE_ID IN
	(
		SELECT temp.TABLE_ID FROM CRE_META_TABLE temp
		WHERE temp.PACKAGE_ID = #{packageId,jdbcType=VARCHAR}
	)

