SELECT LOG_ID as LOG_ID , FOLDER_ID as FOLDER_ID , FOLDER_NAME as FOLDER_NAME, RULE_NAME as RULE_NAME, MODEL_NAME as MODEL_NAME , MODEL_ID as MODEL_ID , VERSION as VERSION , NEW_FOLDER_ID as NEW_FOLDER_ID , NEW_FOLDER_NAME as NEW_FOLDER_NAME, NEW_RULE_NAME as NEW_RULE_NAME , NEW_MODEL_NAME as NEW_MODEL_NAME , NEW_MODEL_ID as NEW_MODEL_ID , NEW_VERSION as NEW_VERSION, OPERATE_TYPE as OPERATE_TYPE , OPERATE_CONTENT as OPERATE_CONTENT , OPERATE_PERSON as OPERATE_PERSON , OPERATE_TIME as OPERATE_TIME , IP as IP FROM CRE_MODEL_OPR_LOG where 1=1 AND RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' )
AND LOG_ID = #{logId,jdbcType=VARCHAR}
AND (FOLDER_ID = #{folderId,jdbcType=VARCHAR} OR NEW_FOLDER_ID = #{folderId,jdbcType=VARCHAR})
AND (MODEL_NAME = #{modelName,jdbcType=VARCHAR} OR NEW_MODEL_NAME = #{modelName,jdbcType=VARCHAR} )
AND (VERSION = #{version,jdbcType=VARCHAR} OR NEW_VERSION = #{version,jdbcType=VARCHAR})
AND NEW_MODEL_ID = #{newModelId,jdbcType=VARCHAR}
AND MODEL_ID = #{modelId,jdbcType=VARCHAR}
AND OPERATE_TYPE = #{operateType,jdbcType=VARCHAR}
AND OPERATE_PERSON = #{operatePerson,jdbcType=VARCHAR}
and OPERATE_TIME >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and OPERATE_TIME <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
AND IP = #{ip,jdbcType=VARCHAR}
ORDER BY OPERATE_TIME DESC

SELECT LOG_ID as LOG_ID , FOLDER_ID as FOLDER_ID , FOLDER_NAME as FOLDER_NAME, RULE_NAME as RULE_NAME, MODEL_NAME as MODEL_NAME , MODEL_ID as MODEL_ID , VERSION as VERSION , NEW_FOLDER_ID as NEW_FOLDER_ID , NEW_FOLDER_NAME as NEW_FOLDER_NAME, NEW_RULE_NAME as NEW_RULE_NAME , NEW_MODEL_NAME as NEW_MODEL_NAME , NEW_MODEL_ID as NEW_MODEL_ID , NEW_VERSION as NEW_VERSION, OPERATE_TYPE as OPERATE_TYPE , OPERATE_CONTENT as OPERATE_CONTENT , OPERATE_PERSON as OPERATE_PERSON , OPERATE_TIME as OPERATE_TIME , IP as IP FROM CRE_MODEL_OPR_LOG where 1=1
AND LOG_ID = #{logId,jdbcType=VARCHAR}
AND (FOLDER_ID = #{folderId,jdbcType=VARCHAR} OR NEW_FOLDER_ID = #{folderId,jdbcType=VARCHAR})
AND (MODEL_NAME = #{modelName,jdbcType=VARCHAR} OR NEW_MODEL_NAME = #{modelName,jdbcType=VARCHAR} )
AND (VERSION = #{version,jdbcType=VARCHAR} OR NEW_VERSION = #{version,jdbcType=VARCHAR})
AND NEW_MODEL_ID = #{newModelId,jdbcType=VARCHAR}
AND MODEL_ID = #{modelId,jdbcType=VARCHAR}
AND OPERATE_TYPE = #{operateType,jdbcType=VARCHAR}
AND OPERATE_PERSON = #{operatePerson,jdbcType=VARCHAR}
and OPERATE_TIME >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and OPERATE_TIME <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
AND IP = #{ip,jdbcType=VARCHAR}
ORDER BY OPERATE_TIME DESC

