mapper.select:
SELECT ID as "id", LOG_ID as "logId", NODE_ID as "nodeId", NODE_NAME as "nodeName", NODE_TYPE as "nodeType", STATE as "state", START_TIME as "startTime", END_TIME as "endTime", RESULT as "result" FROM CRE_RULE_LOG_DETAIL WHERE 1=1
AND NODE_TYPE = #{nodeType,jdbcType=VARCHAR}
AND LOG_ID = ( SELECT EXECUTOR_RULE_LOG_ID FROM CRE_MODEL_TEST_LOG WHERE 1=1
AND LOG_ID = #{modelTrialLogId,jdbcType=VARCHAR}
) ORDER BY START_TIME

select a.LOG_ID, a.RULE_ID,b.RULE_NAME, a.FOLDER_ID, a.STATE, a.HIT_RULE_NUM,
a.START_TIME, a.END_TIME, a.INPUT_DATA, a.OUTPUT_DATA,a.EXCEPTION
from CRE_RULE_LOG a,CRE_RULE_DETAIL b
where a.RULE_ID = b.RULE_ID

    AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}


    AND a.RULE_ID = #{ruleId,jdbcType=VARCHAR}


    AND a.STATE = #{state,jdbcType=VARCHAR}

order by a.START_TIME DESC

SELECT TO_CHAR(START_TIME,'yyyy-mm-dd') as "time" , COUNT(CASE WHEN HIT_RULE_NUM>0 THEN 1 ELSE null END )AS "hitRuleNum", COUNT(HIT_RULE_NUM) AS "allRuleNum", (COUNT(HIT_RULE_NUM) - COUNT(CASE WHEN HIT_RULE_NUM>0 THEN 1 ELSE null END ) ) as "notHitRuleNum", case when COUNT(HIT_RULE_NUM)*100 >0 then round(COUNT(CASE WHEN HIT_RULE_NUM>0 THEN 1 ELSE null END )/COUNT(HIT_RULE_NUM)*100,2) else 0 end "value", '命中率' "name" FROM CRE_RULE_LOG WHERE START_TIME >= TRUNC(SYSDATE - #{day})
AND FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND RULE_ID = #{ruleId,jdbcType=VARCHAR}
GROUP BY TO_CHAR(START_TIME,'yyyy-mm-dd') order by TO_CHAR(START_TIME,'yyyy-mm-dd') asc

SELECT TO_CHAR(a.START_TIME,'yyyy-mm-dd') as "time" , COUNT(CASE WHEN a.HIT_RULE_NUM>0 THEN 1 ELSE null END )AS "hitRuleNum", COUNT(a.HIT_RULE_NUM) AS "allRuleNum", (COUNT(a.HIT_RULE_NUM) - COUNT(CASE WHEN a.HIT_RULE_NUM>0 THEN 1 ELSE null END ) ) as "notHitRuleNum", case when COUNT(a.HIT_RULE_NUM)*100 >0 then round(COUNT(CASE WHEN a.HIT_RULE_NUM>0 THEN 1 ELSE null END )/COUNT(a.HIT_RULE_NUM)*100,2) else 0 end "value", '命中率' "name" FROM CRE_RULE_LOG a LEFT JOIN CRE_RULE_DETAIL c on a.RULE_ID = c.RULE_ID WHERE a.START_TIME >= TRUNC(SYSDATE - #{day}) AND c.RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' )
AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND a.RULE_ID = #{ruleId,jdbcType=VARCHAR}
GROUP BY TO_CHAR(a.START_TIME,'yyyy-mm-dd') order by TO_CHAR(a.START_TIME,'yyyy-mm-dd') asc

SELECT TO_CHAR(a.START_TIME,'yyyy-mm-dd') "time", COUNT(a.START_TIME) "num",a.STATE as "state"
FROM CRE_RULE_LOG a

    LEFT JOIN
    CRE_RULE_DETAIL b
    ON a.RULE_ID = b.RULE_ID

WHERE START_TIME >= TRUNC(SYSDATE - #{day})

    AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}


    AND RULE_ID = #{ruleId,jdbcType=VARCHAR}


    AND b.DEPT_ID = #{deptId,jdbcType=VARCHAR}


    AND a.STATE = #{state,jdbcType=VARCHAR}

GROUP BY TO_CHAR(a.START_TIME,'yyyy-mm-dd'),a.STATE order by TO_CHAR(a.START_TIME,'yyyy-mm-dd') asc

SELECT TO_CHAR(a.START_TIME,'yyyy-mm-dd') "time", COUNT(a.START_TIME) "num",a.STATE as "state" FROM CRE_RULE_LOG a
LEFT JOIN CRE_RULE_DETAIL b ON a.RULE_ID = b.RULE_ID
WHERE START_TIME >= TRUNC(SYSDATE - #{day}) AND b.RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' )
AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND RULE_ID = #{ruleId,jdbcType=VARCHAR}
AND b.DEPT_ID = #{deptId,jdbcType=VARCHAR}
AND a.STATE = #{state,jdbcType=VARCHAR}
GROUP BY TO_CHAR(a.START_TIME,'yyyy-mm-dd'),a.STATE order by TO_CHAR(a.START_TIME,'yyyy-mm-dd') asc

SELECT COUNT(a.START_TIME) "value", b.FOLDER_NAME "name", b.FOLDER_ID "folderId", 'folderId' "id" FROM CRE_RULE_LOG a,CRE_RULE_FOLDER b WHERE a.START_TIME >= TRUNC(SYSDATE - #{day}) AND a.FOLDER_ID=b.FOLDER_ID
AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND a.RULE_ID = #{ruleId,jdbcType=VARCHAR}
AND a.STATE = #{state,jdbcType=VARCHAR}
GROUP BY b.FOLDER_NAME,b.FOLDER_ID

SELECT COUNT(CASE WHEN HIT_RULE_NUM>0 THEN 1 ELSE null END )AS "hitRuleNum",
COUNT(HIT_RULE_NUM) AS "allRuleNum",
case when COUNT(HIT_RULE_NUM)*100 >0 then
round(COUNT(CASE WHEN HIT_RULE_NUM>0 THEN 1 ELSE null END )/COUNT(HIT_RULE_NUM)*100,2)
else
0
end "value",
'命中率' "name"
FROM CRE_RULE_LOG WHERE START_TIME >= TRUNC(SYSDATE - #{day})

    AND FOLDER_ID = #{folderId,jdbcType=VARCHAR}


    AND RULE_ID = #{ruleId,jdbcType=VARCHAR}


    AND RULE_ID = #{deptId,jdbcType=VARCHAR}

SELECT * FROM ( SELECT A .LOG_ID, A .RULE_ID, A .FOLDER_ID, A .STATE, A .HIT_RULE_NUM, A .START_TIME, A .END_TIME, B.FOLDER_NAME, C.MODULE_NAME, C.RULE_NAME, C.VERSION, '0' as IS_DRAFT FROM CRE_RULE_LOG A INNER JOIN CRE_RULE_FOLDER B ON A .FOLDER_ID = B.FOLDER_ID LEFT JOIN (select RULE_NAME,MODULE_NAME,RULE_ID,VERSION,IS_DEL from CRE_RULE_DETAIL UNION ALL select RULE_NAME,MODULE_NAME,RULE_ID,VERSION,IS_DEL from CRE_RULE_DETAIL_DRAFT) C ON A .RULE_ID = C.RULE_ID WHERE C.IS_DEL = '0'
AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND a.RULE_ID = #{ruleId,jdbcType=VARCHAR}
AND C.MODULE_NAME like #{pattern}
and STATE = #{state,jdbcType=TIMESTAMP}
and START_TIME >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and START_TIME <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
) WHERE 1=1
AND IS_DRAFT = #{isDraft,jdbcType=VARCHAR}
order by
${sort}
${order}
START_TIME DESC

SELECT * FROM ( SELECT A .LOG_ID, A .RULE_ID, A .FOLDER_ID, A .STATE, A .HIT_RULE_NUM, A .START_TIME, A .END_TIME, B.FOLDER_NAME, C.MODULE_NAME, C.RULE_NAME, C.VERSION, '0' as IS_DRAFT FROM CRE_RULE_LOG A INNER JOIN CRE_RULE_FOLDER B ON A .FOLDER_ID = B.FOLDER_ID LEFT JOIN (select RULE_NAME,MODULE_NAME,RULE_ID,VERSION,IS_DEL from CRE_RULE_DETAIL UNION ALL select RULE_NAME,MODULE_NAME,RULE_ID,VERSION,IS_DEL from CRE_RULE_DETAIL_DRAFT) C ON A .RULE_ID = C.RULE_ID WHERE C.IS_DEL = '0'
AND a.FOLDER_ID = #{folderId,jdbcType=VARCHAR}
AND a.RULE_ID = #{ruleId,jdbcType=VARCHAR}
AND C.MODULE_NAME like #{pattern}
and STATE = #{state,jdbcType=TIMESTAMP}
and START_TIME >= to_date(#{startDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
and START_TIME <= to_date(#{endDate,jdbcType=TIMESTAMP} ,'yyyy-mm-dd hh24:mi:ss')
) WHERE 1=1
AND IS_DRAFT = #{isDraft,jdbcType=VARCHAR}
AND RULE_NAME IN ( SELECT DISTINCT (A .RESOURCE_ID) FROM CRE_AUTH_AUTHORITY A WHERE A .SUBJECT_ID IN
#{item}
AND A .RESOURCE_TYPE_ID IN ('3', '8') AND A .RESOURCE_ID != '*' ) order by
${sort}
${order}
START_TIME DESC
